changeSet.sql:
create schema personal;
create table personal.users ( id bigserial not null, constraint personal_pk primary key (id) );
create schema task;
create table task.projects ( id bigserial not null, name varchar, owner_id int not null, constraint project_pk primary key (id), constraint project_personal_fk foreign key (owner_id) references personal.users(id) );
create table task.tasks ( id bigserial not null, content varchar not null, title varchar not null, priority varchar not null, status varchar not null, creator_id int not null, assignee_id int not null, parent_id int not null, constraint task_pk primary key (id), constraint task_personal_creator_fk foreign key (creator_id) references personal.users(id), constraint task_personal_assignee_fk foreign key (assignee_id) references personal.users(id), constraint task_project_fk foreign key (parent_id) references task.projects(id) );

alter table task.tasks add column created_time date;
alter table task.tasks add column update_time date;
alter table task.projects add column created_time date;
alter table task.projects add column update_time date;
